// Enhanced search function with relevance scoring
export const performSearch = (query) => {
  if (!query || query.trim() === '') return [];

  const searchTerm = query.toLowerCase().trim();
  const words = searchTerm.split(' ').filter(word => word.length > 0);

  const results = searchIndex.map(item => {
    let score = 0;
    let matchedIn = [];

    // Exact title match (highest priority)
    if (item.title.toLowerCase().includes(searchTerm)) {
      score += 100;
      matchedIn.push('title');
    }

    // Exact content match
    if (item.content.toLowerCase().includes(searchTerm)) {
      score += 50;
      matchedIn.push('content');
    }

    // Exact section match
    if (item.section.toLowerCase().includes(searchTerm)) {
      score += 30;
      matchedIn.push('section');
    }

    // Keyword exact match
    if (item.keywords.some(keyword => keyword.includes(searchTerm))) {
      score += 40;
      matchedIn.push('keywords');
    }

    // Individual word matches
    words.forEach(word => {
      if (item.title.toLowerCase().includes(word)) score += 20;
      if (item.content.toLowerCase().includes(word)) score += 10;
      if (item.keywords.some(keyword => keyword.includes(word))) score += 15;
    });

    // Type bonuses
    if (item.type === 'campaign') score += 15;
    if (item.type === 'article') score += 5;
    if (item.type === 'opportunity') score += 10;

    return {
      ...item,
      score,
      matchedIn,
      snippet: generateSnippet(item.content, searchTerm)
    };
  }).filter(item => item.score > 0);

  // Sort by relevance score
  return results.sort((a, b) => b.score - a.score).slice(0, 10);
};

// Generate snippet with highlighted search term
const generateSnippet = (content, searchTerm) => {
  const index = content.toLowerCase().indexOf(searchTerm.toLowerCase());
  if (index === -1) return content.substring(0, 100) + '...';

  const start = Math.max(0, index - 40);
  const end = Math.min(content.length, index + searchTerm.length + 60);
  
  let snippet = content.substring(start, end);
  if (start > 0) snippet = '...' + snippet;
  if (end < content.length) snippet = snippet + '...';
  
  return snippet;
};